CREATE DEFINER=`root`@`localhost` PROCEDURE `spExistenciasCategoria`(IN sucursal INT)
BEGIN
    SELECT
        c.CategoriaID,
        c.Nombre AS Categoria,
        SUM(i.Existencia) AS 'Existencia Actual',
        CASE
            WHEN c.CategoriaID IN (1, 6, 7, 8) THEN 'Unidad'
            WHEN c.CategoriaID IN (2, 3, 5) THEN 'Cajas'
        END AS 'Tipo Unidad'
    FROM
        Categoria AS c
    INNER JOIN
        producto_detalle AS pd ON c.CategoriaID = pd.CategoriaID
    INNER JOIN
        inventario AS i ON pd.ProductoID = i.ProductoID
    WHERE
        i.SucursalID = sucursal or sucursal = 0
    GROUP BY
        pd.CategoriaID
	order by SUM(i.Existencia);
END ;;
