CREATE DEFINER=`root`@`localhost` PROCEDURE `spProductosProximosCaducar`(IN sucursal INT)
BEGIN
    SELECT
        pd.Nombre AS Producto,
        i.FechaExp AS 'Fecha de Caducidad',
        DATEDIFF(i.FechaExp, CURDATE()) AS 'Dias Restantes',
        i.Existencia AS Cantidad,
        CASE pd.CategoriaID
            WHEN 1 THEN 'Unidad'
            WHEN 2 THEN 'Cajas'
            WHEN 3 THEN 'Cajas'
            WHEN 5 THEN 'Cajas'
            WHEN 6 THEN 'Unidad'
            WHEN 7 THEN 'Unidad'
            WHEN 8 THEN 'Unidad'
        END AS 'Tipo Unidad',
        c.Nombre AS Categoria,
        s.Ciudad as Sucursal,
        prv.Nombre AS Proveedor
    FROM
        producto_detalle AS pd
    INNER JOIN
        inventario AS i ON pd.productoID = i.productoID
    INNER JOIN
        categoria AS c ON pd.CategoriaID = c.CategoriaID
    INNER JOIN
        Sucursal AS s ON i.SucursalID = s.SucursalID
    INNER JOIN
        Proveedor AS prv ON pd.ProveedorID = prv.ProveedorID
    WHERE
        DATEDIFF(i.FechaExp, CURDATE()) BETWEEN 0 AND 90 AND (i.SucursalId = sucursal OR sucursal = 0)
	order by DATEDIFF(i.FechaExp, CURDATE());
END ;;
