CREATE DEFINER=`root`@`localhost` PROCEDURE `spSalidaInventario`(IN fecha_inicio datetime, IN fecha_final datetime, IN sucursal int)
BEGIN
    SELECT
        pd.Nombre AS 'Nombre del Producto',
        dos.Cantidad_Solicitada AS 'Cantidad',
        CASE
            WHEN pd.categoriaID IN (1, 6, 7, 8) THEN 'Unidad'
            WHEN pd.categoriaID IN (2, 3, 5) THEN 'Cajas'
