    CONCAT('L. ', CAST(SUM(CASE WHEN OE.Fecha_Hora BETWEEN CONCAT(p_year, '-01-01') AND CONCAT(p_year, '-03-31') THEN DE.Precio_Unitario ELSE PD.precio END) AS DECIMAL(10, 2))) AS 'Primer Trimestre',
    CONCAT('L. ', CAST(SUM(CASE WHEN OE.Fecha_Hora BETWEEN CONCAT(p_year, '-04-01') AND CONCAT(p_year, '-06-30') THEN DE.Precio_Unitario ELSE PD.precio END) AS DECIMAL(10, 2))) AS 'Segundo Trimestre',
    '' AS 'Tercer Trimestre',
    '' AS 'Cuarto Trimestre',
    CONCAT('L. ', CAST((SUM(CASE WHEN OE.Fecha_Hora BETWEEN CONCAT(p_year, '-01-01') AND CONCAT(p_year, '-03-31') THEN DE.Precio_Unitario ELSE PD.precio END) + 
          SUM(CASE WHEN OE.Fecha_Hora BETWEEN CONCAT(p_year, '-04-01') AND CONCAT(p_year, '-06-30') THEN DE.Precio_Unitario ELSE PD.precio END)) / 2 AS DECIMAL(10, 2))) AS 'PrecioPromedio'
FROM
    Producto_Detalle PD
LEFT JOIN
    Detalles_Orden_Entrada DE ON PD.ProductoID = DE.ID_Producto
LEFT JOIN
    Ordenes_Entrada OE ON DE.ID_Orden_Entrada = OE.ID_Orden_Entrada
WHERE
	YEAR(OE.Fecha_Hora) = p_year
GROUP BY
    PD.ProductoID, PD.Nombre
order by
	PD.Nombre;
END ;;
