CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_productos_sin_existencia`()
BEGIN
    SELECT pd.productoid AS `Producto ID`, 
           pd.nombre AS `Nombre`, 
           c.nombre AS `Categoría`,
           pr.nombre AS `Proveedor`, 
           CONCAT('L.', pd.precio) AS `Precio`,
           pd.existenciaminima AS `Existencia Mínima`,
			CASE
				WHEN c.categoriaID IN (1, 6, 7, 8) THEN 'Unidad'
				WHEN c.categoriaID IN (2, 3, 5) THEN 'Cajas'
			END AS 'Tipo Unidad'
    FROM producto_detalle pd
    LEFT JOIN detalles_orden_salida dos ON pd.productoid = dos.id_producto
    INNER JOIN categoria c ON pd.categoriaid = c.categoriaid
    INNER JOIN proveedor pr ON pd.proveedorid = pr.proveedorid
    LEFT JOIN inventario inv ON pd.productoid = inv.productoid
    WHERE inv.existencia IS NULL OR inv.existencia = 0
    GROUP BY pd.productoid, pd.nombre, c.nombre, pr.nombre, pd.precio, pd.existenciaminima
    Order by pd.nombre;
END ;;
